    return (
        <div>
            <h1>Todo List</h1>
        <AddTodo />
            <ul>
                {todos.map((todo) => {
                    return (
                        <li key={todo.id}>
                            <span>{todo.text}</span>
                            <MdDelete onClick={() => handleDelete(todo.id)} />
                        </li>
                    )
                })}

            </ul>
        </div>


    )
}
